package com.moodanalyser;

public class MoodAnalyser {
	

	
    public String analysemood(String message) {
    	try {
       if(message.contains("Sad"))
    		   return "SAD";
       else 
    	   return "HAPPY";
    }catch(NullPointerException e)
    	{
    	System.out.println("Exception Thrown");
    	}
    	return "HAPPY";
    
    
}}